                                            MULTIPLIER: Config.CREATURE.DEFAULT_MONEY.MULTIPLIER * 4
                                        }
                                    }
                                },
                                displayName: `${Config.CURRENCY_NAME} stash`,
                                perPlayer: true,
                            },
                            DYNAMIC_SECRET_KEY: {
                                minMax: [1, 1],
                                extraReward: {
                                    items: [{ itemType: secreteKeyFragmentId as any }]
                                }
                            }
                        },
