                            normalRoom: [{
                                amount: [3, 4],
                                type: zoneId
                            }],
                            lastRoom: [{
                                amount: [4, 6],
                                type: zoneId,
                            },
                            {
                                amount: [1, 1],
                                type: `BOSS_${zoneId}`,
                            }]
                        },
                    }
                });
