                CreatureSetting.values.push(new CreatureSetting({
                    name: `BOSS_${zoneId}`,
                    names: bossNames,
                    traitChance: 0.5,
                    weight: 100,
                    ...DEFAULT_OPTIONS,
                    healthFactor: 15,
                    damageFactor: 2,
                    expConfig: {
                        ...DEFAULT_EXP,
                        MULTIPLIER: DEFAULT_EXP.MULTIPLIER * 15
                    },
                    moneyConfig: {
                        ...Config.CREATURE.DEFAULT_MONEY,
                        MULTIPLIER: Config.CREATURE.DEFAULT_MONEY.MULTIPLIER * 15
                    },
                    drops: [...CreatureSetting.DEFAULT_DROPS,
                    { itemType: "UNKNOWN_EQUIPMENT", amount: 1 },
                    { itemType: "UNKNOWN_EQUIPMENT", amount: 1 },
                    { itemType: "UNKNOWN_EQUIPMENT", amount: 1 },
                    { itemType: "UNKNOWN_EQUIPMENT", amount: 1 },
